INSERT INTO fish(merchID, species, genus, commonName, price, isInvertebrate, waterType, phRange, description) VALUES(NEXTVAL('merchSeq'), 'Xiphophorus', 'maculatus', 'Blue Wag Platy', 2.56, FALSE, 'fresh', '7.0-8.0', 'The southern platyfish, common platy or moonfish (Xiphophorus maculatus) is a species of freshwater fish in family Poecilidae of order Cyprinodontiformes. A live-bearer, it is closely related to the green swordtail (X. helleri) and can interbreed with it.');
